package session10;

public class Main {
	
	int x; // attribute of class
	
	Main() //constructor
	{
		x = 6; //intialize the object
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Main obj = new Main(); //this will call constructor
		System.out.println(obj.x); // print value of x

	}

}
